import React, { useState } from 'react';
import TodoItem from './components/TodoItem';
import './styles/App.css';

function App() {
    const [leftTodos, setLeftTodos] = useState([]);
    const [rightTodos, setRightTodos] = useState([]);
    const [inputValue, setInputValue] = useState('');

    const addTodo = (setTodos, todos) => {
        if (inputValue.trim()) {
            setTodos([...todos, { title: inputValue, completed: false }]);
            setInputValue('');
        }
    };

    const toggleTodo = (setTodos, todos, index) => {
        const newTodos = todos.map((todo, i) =>
            i === index ? { ...todo, completed: !todo.completed } : todo
        );
        setTodos(newTodos);
    };

    const removeTodo = (setTodos, todos, index) => {
        const newTodos = todos.filter((_, i) => i !== index);
        setTodos(newTodos);
    };

    return (
        <div className="App">
            <h1>ToDo List</h1>
            <input
                type="text"
                value={inputValue}
                onChange={(e) => setInputValue(e.target.value)}
                placeholder="Add a new todo"
            />
            <div className="buttons">
                <button onClick={() => addTodo(setLeftTodos, leftTodos)}>Add to Left</button>
                <button onClick={() => addTodo(setRightTodos, rightTodos)}>Add to Right</button>
            </div>
            <div className="cards">
                <div className="card">
                    <h2>Left</h2>
                    <ul>
                        {leftTodos.map((todo, index) => (
                            <TodoItem
                                key={index}
                                title={todo.title}
                                completed={todo.completed}
                                onToggle={() => toggleTodo(setLeftTodos, leftTodos, index)}
                                onRemove={() => removeTodo(setLeftTodos, leftTodos, index)}
                            />
                        ))}
                    </ul>
                </div>
                <div className="card">
                    <h2>Right</h2>
                    <ul>
                        {rightTodos.map((todo, index) => (
                            <TodoItem
                                key={index}
                                title={todo.title}
                                completed={todo.completed}
                                onToggle={() => toggleTodo(setRightTodos, rightTodos, index)}
                                onRemove={() => removeTodo(setRightTodos, rightTodos, index)}
                            />
                        ))}
                    </ul>
                </div>
            </div>
        </div>
    );
}

export default App;
